public class RepeatedSubstringPattern {
    public boolean repeatedSubstringPattern(String s){
        //没错，但是慢。
        int lenOfSub = 1;
        while(lenOfSub <= s.length() / 2){
            //多一个判断，少做很多事情。
            if(s.length() % lenOfSub == 0) {
                StringBuilder sub = new StringBuilder();
                StringBuilder newS = new StringBuilder();
                for (int i = 0; i < lenOfSub; i++) {
                    sub.append(s.charAt(i));
                }
                while (newS.length() < s.length()) {
                    newS.append(sub);
                }
                String newS1 = newS.toString();
                if (newS1.equals(s)) {
                    return true;
                }
            }
            lenOfSub++;
        }
        return false;
        //快，但是有错
//        if(s.length() == 1){
//            return false;
//        }
//        for(int i = 0; i < s.length() / 2; i++){
//            if(s.charAt(i) != s.charAt(s.length() / 2 + i)){
//                return false;
//            }
//        }
//        return true;
    }

    public static void main(String[] args){
        RepeatedSubstringPattern r = new RepeatedSubstringPattern();
        String s = "";
        boolean ret = r.repeatedSubstringPattern(s);
        System.out.println(ret);
    }
}
